def parse_sequence_from_pdb(lines, chain_id):
    sequence = ''
    for line in lines:
        if line[:6] == 'SEQRES' and line[11] == chain_id:
            for res in line[19:].split():
                sequence += RES_NAME_TO_SYM[res]
    return sequence


if __name__ == '__main__':

    if not os.path.exists(os.path.join(DATA_FOLDER, 'training_set')):
        os.makedirs(os.path.join(DATA_FOLDER, 'training_set'))

    fa_lines = list()

    for prot_name in PROT_NAMES:

        folder = os.path.join(DATA_FOLDER, 'training_set', prot_name)
        
        if not os.path.exists(os.path.join(folder, 'sequence.fa')):
            print('Creating folder %s' % prot_name)

            pdb_id, chain_id = prot_name[:-1], prot_name[-1]
            pdb = pypdb.get_pdb_file(pdb_id)
            sequence = parse_sequence_from_pdb(pdb.split('\n'), chain_id)

            if not os.path.exists(folder):
                os.makedirs(folder)
            with open(os.path.join(folder, 'native.pdb'), 'w') as f:
                f.write(pdb)

            with open(os.path.join(folder, 'sequence.fa'), 'w') as f:
                f.write('>%s\n' % prot_name)
                f.write('%s' % sequence)
        else:
            with open(os.path.join(folder, 'sequence.fa'), 'r') as f:
                fa_lines.append(f.readline())
                fa_lines.append(f.readline() + '\n')

    with open(os.path.join(DATA_FOLDER, 'sequences.fa'), 'w') as f:
        for line in fa_lines:
            f.write(line)
